SELECT 
    @sl_dk_no = SUM(ISNULL(_rc.so_luong,0)), @vnd_dk_no = SUM(ISNULL(_rc.vnd,0))
FROM dbo._rc_detail d 
WHERE _rc.SubsidiaryId = @SubId AND _rc.tk_no LIKE CONCAT(@Tk,''%'') AND _rc.ngay_ct < @FromDate AND _rc.vthhtp = @ProductId
GROUP BY _rc.tk_no

SELECT 
