    (ISNULL(@sl_dk_no,0) - ISNULL(@sl_dk_co,0)) sl_dau_ky, 
	(ISNULL(@sl_dvt2_dk_no,0) - ISNULL(@sl_dvt2_dk_co,0)) sl_dvt2_dau_ky, 
    (ISNULL(@vnd_dk_no,0) - ISNULL(@vnd_dk_co,0)) vnd_dau_ky, 
    @FromDate tu_ngay, 
