    @tk_title Tk_Title,
    @unit_name unit_name,
    @vthhtp_ProductCode vthhtp_ProductCode,
    @vthhtp_ProductName vthhtp_ProductName,
	@json_data json_data





SELECT CONCAT( N''Đơn vị: '',unitName) AS unitName,
CONCAT(N''Đầu kỳ: '', ISNULL( tonDauKy,0)) AS tonDauKy
FROM OPENJSON(@json_data)
