;WITH ps AS (
	SELECT 
		g.ProductionStepId,
		ps.StepId,
		po.ProductionOrderCode,
		po.ProductionOrderId,
		ld.LinkDataObjectId,
		ld.LinkDataObjectTypeId,
		SUM(ld.QuantityOrigin - ISNULL(ld.OutsourcePartQuantity,0) - ISNULL(ld.OutsourceQuantity,0)) MaterialQuantity
	FROM [ManufacturingDB].[dbo].ProductionStep ps 
	INNER JOIN [ManufacturingDB].[dbo].ProductionOrder po 
		ON ps.ContainerId = po.ProductionOrderId 
			AND ps.ContainerTypeId = 2 
			AND ps.IsGroup = 1 
			AND ps.IsDeleted = 0 
			AND ps.IsFinish = 0
	INNER JOIN [ManufacturingDB].[dbo].ProductionStep g 
		ON ps.ProductionStepId = g.ParentId
			AND g.IsGroup = 0
			AND g.IsDeleted = 0 
			AND g.IsFinish = 0
			AND g.IsDeleted = 0
	INNER JOIN [ManufacturingDB].[dbo].ProductionStepLinkDataRole lr ON g.ProductionStepId = lr.ProductionStepId AND lr.ProductionStepLinkDataRoleTypeId = @PRODUCTION_STEP_LINK_DATA_ROLE_TYPE_OUT
	INNER JOIN [ManufacturingDB].[dbo].ProductionStepLinkData ld ON lr.ProductionStepLinkDataId = ld.ProductionStepLinkDataId AND ld.IsDeleted = 0
	JOIN @LstProductionOrderStatus s ON po.ProductionOrderStatus = s.[Status]
	--WHERE po.ProductionOrderStatus IN (1,2,4) 
		WHERE (@FromDate IS NULL OR @ToDate IS NULL OR po.Date BETWEEN @FromDate AND @ToDate)
		AND g.SubsidiaryId = @SubId
		AND (ISNULL(@StepId,0) = 0 OR ps.StepId = @StepId) --[A1]
	GROUP BY g.ProductionStepId, ps.StepId, po.ProductionOrderId, po.ProductionOrderCode, ld.LinkDataObjectId, ld.LinkDataObjectTypeId
),
handover AS (
		SELECT  
				po.ProductionOrderId,
		ps.StepId,
				h.FromDepartmentId DepartmentId,
				h.ObjectId, 
				h.ObjectTypeId,
				ISNULL(SUM(h.HandoverQuantity),0) HandoverQuantity
		FROM [ManufacturingDB].[dbo].ProductionHandover h
		INNER JOIN [ManufacturingDB].[dbo].ProductionStep ps ON h.FromProductionStepId = ps.ProductionStepId AND ps.IsDeleted = 0
		INNER JOIN [ManufacturingDB].[dbo].ProductionOrder po ON ps.ContainerId = po.ProductionOrderId AND ps.ContainerTypeId = @CONTAINER_TYPE_PRODUCTION_ORDER 
		JOIN @LstProductionOrderStatus s ON po.ProductionOrderStatus = s.[Status]
		WHERE h.[Status] = 1 AND h.IsDeleted = 0 --AND po.ProductionOrderStatus IN (1,2,4) 
			AND (@FromDate IS NULL OR @ToDate IS NULL OR po.Date BETWEEN @FromDate AND @ToDate)
			AND (ISNULL(@StepId,0) = 0 OR ps.StepId = @StepId) --[A1]
		GROUP BY po.ProductionOrderId, ps.StepId, h.FromDepartmentId, h.ObjectId, h.ObjectTypeId
),
import AS (
SELECT 
				po.ProductionOrderId,
		ps.StepId,
				ir.DepartmentId,
				id.ProductId,
				ISNULL(SUM(ISNULL(id.PrimaryQuantity,0)),0) ImportQuantity
		FROM StockDB.dbo.InventoryDetail id
		INNER JOIN StockDB.dbo.Inventory i ON id.InventoryId = i.InventoryId AND id.IsDeleted = 0 AND i.IsDeleted = 0 AND id.SubsidiaryId = i.SubsidiaryId 
		INNER JOIN StockDB.dbo.InventoryRequirementDetail ird ON id.InventoryRequirementDetailId = ird.InventoryRequirementDetailId AND ird.IsDeleted = 0 AND id.SubsidiaryId = ird.SubsidiaryId 
		INNER JOIN StockDB.dbo.InventoryRequirement ir ON ird.InventoryRequirementId = ir.InventoryRequirementId AND ir.IsDeleted = 0 AND id.SubsidiaryId = ir.SubsidiaryId 
		INNER JOIN [ManufacturingDB].[dbo].ProductionStep ps ON ird.ProductionStepId = ps.ProductionStepId AND ps.IsDeleted = 0
		INNER JOIN [ManufacturingDB].[dbo].ProductionOrder po ON ps.ContainerId = po.ProductionOrderId AND ps.ContainerTypeId = @CONTAINER_TYPE_PRODUCTION_ORDER 
		JOIN @LstProductionOrderStatus s ON po.ProductionOrderStatus = s.[Status]--AND po.ProductionOrderStatus IN (1,2,4)
		WHERE i.InventoryTypeId = @INVENTORY_TYPE_INPUT AND i.IsApproved = 1 AND ir.CensorStatus = @CENSOR_STATUS_APPROVED AND ir.ProductionOrderId > 0 AND ird.ProductionStepId > 0 AND ir.DepartmentId > 0   AND (@FromDate IS NULL OR @ToDate IS NULL OR po.Date BETWEEN @FromDate AND @ToDate)
		AND (ISNULL(@DepartmentId,0) = 0 OR ir.DepartmentId = @DepartmentId) AND (ISNULL(@StepId,0) = 0 OR ps.StepId = @StepId) --[A1]
		GROUP BY po.ProductionOrderId, ps.StepId, ir.DepartmentId, id.ProductId
)
INSERT INTO @ProductionMaterial
SELECT
	v.StepId,
	v.DepartmentId,
	v.ProductionOrderId,
	v.ProductionOrderCode,
	v.LinkDataObjectId, 
	v.LinkDataObjectTypeId,
	v.MaterialQuantity - ISNULL(ho.HandoverQuantity, 0) - ISNULL(imp.ImportQuantity, 0) MaterialQuantity,
	CASE 
		WHEN v.LinkDataObjectTypeId = @LINK_DATA_OBJECT_TYPE_PRODUCT
		THEN CONCAT(p.ProductCode, '' / '', p.ProductName)
		ELSE sm.Title
	END MaterialTitle,
	CONCAT(d.DepartmentName, '' / '', s.StepName) GroupTitle
FROM (
	SELECT 
		ps.StepId,
		a.DepartmentId,
		ps.ProductionOrderId,
		ps.ProductionOrderCode,
		ps.LinkDataObjectId, 
		ps.LinkDataObjectTypeId,
		SUM(
			CASE
				WHEN (ld.QuantityOrigin - ISNULL(ld.OutsourcePartQuantity,0) - ISNULL(ld.OutsourceQuantity,0)) > 0 
				THEN ps.MaterialQuantity * a.AssignmentQuantity / (ld.QuantityOrigin - ISNULL(ld.OutsourcePartQuantity,0) - ISNULL(ld.OutsourceQuantity,0))
				ELSE 0
			END
		) MaterialQuantity
	FROM [ManufacturingDB].[dbo].ProductionAssignment a
	INNER JOIN ps ps ON a.ProductionStepId = ps.ProductionStepId
	INNER JOIN [ManufacturingDB].[dbo].ProductionStepLinkData ld ON a.ProductionStepLinkDataId = ld.ProductionStepLinkDataId AND ld.IsDeleted = 0
	WHERE (ISNULL(@DepartmentId,0) = 0 OR a.DepartmentId = @DepartmentId) AND (ISNULL(@StepId,0) = 0 OR ps.StepId = @StepId) --[A1]
	GROUP BY ps.StepId, a.DepartmentId, ps.ProductionOrderId, ps.ProductionOrderCode, ps.LinkDataObjectId, ps.LinkDataObjectTypeId
) v
LEFT JOIN [StockDB].[dbo].Product p ON v.LinkDataObjectId = p.ProductId AND v.LinkDataObjectTypeId = @LINK_DATA_OBJECT_TYPE_PRODUCT
LEFT JOIN [ManufacturingDB].[dbo].ProductSemi sm ON v.LinkDataObjectId = sm.ProductSemiId AND v.LinkDataObjectTypeId = @LINK_DATA_OBJECT_TYPE_SEMI
LEFT JOIN handover ho ON v.ProductionOrderId = ho.ProductionOrderId AND v.DepartmentId = ho.DepartmentId AND v.StepId = ho.StepId AND v.LinkDataObjectId = ho.ObjectId AND v.LinkDataObjectTypeId = ho.ObjectTypeId
LEFT JOIN import imp ON v.ProductionOrderId = imp.ProductionOrderId AND v.DepartmentId = imp.DepartmentId AND v.StepId = imp.StepId AND v.LinkDataObjectId = imp.ProductId AND v.LinkDataObjectTypeId = @LINK_DATA_OBJECT_TYPE_PRODUCT
LEFT JOIN [ManufacturingDB].[dbo].v_Department d on v.DepartmentId = d.F_Id
LEFT JOIN [ManufacturingDB].[dbo].Step s on v.StepId = s.StepId;

