UPDATE [dbo].[ReportType] SET [ReportTypeName]=N'Sổ chi tiết thống kê sản xuất và bàn giao', [UpdatedByUserId]=170, [UpdatedDatetimeUtc]='2023-08-01 03:22:31.3802733', [BodySql]=N'DECLARE @PRODUCTION_STEP_LINK_DATA_ROLE_TYPE_IN INT = 1;
DECLARE @PRODUCTION_STEP_LINK_DATA_ROLE_TYPE_OUT INT = 2;
DECLARE @CONTAINER_TYPE_PRODUCTION_ORDER INT = 2;
DECLARE @LINK_DATA_OBJECT_TYPE_PRODUCT INT = 1;
DECLARE @LINK_DATA_OBJECT_TYPE_SEMI INT = 2;


DECLARE @WorkloadType_Quantity INT = 1;
DECLARE @WorkloadType_Purity INT = 2;
DECLARE @TargetProductivityId_Default INT;

SELECT @TargetProductivityId_Default = TargetProductivityId FROM dbo.TargetProductivity WHERE IsDefault = 1 AND IsDeleted = 0;

WITH prod
  AS (SELECT ROW_NUMBER() OVER (PARTITION BY d.ProductionOrderId ORDER BY d.Quantity DESC) RNumber,
             d.ProductionOrderId,
             d.ProductId,
             d.OrderCode,
             d.Quantity + d.ReserveQuantity ProductProductionQuantity
        FROM dbo.ProductionOrderDetail d
       WHERE d.IsDeleted    = 0
         AND d.SubsidiaryId = @SubId),
     handoverSum
  AS (SELECT ho.FromProductionStepId,
             ho.HandoverDatetime,
             ho.ObjectTypeId,
             ho.ObjectId,
             SUM(ho.HandoverQuantity) SumHandoverQuantity
        FROM dbo.ProductionHandover ho
       WHERE ho.IsDeleted = 0
       GROUP BY ho.FromProductionStepId,
                ho.HandoverDatetime,
                ho.ObjectTypeId,
                ho.ObjectId),
     handover
  AS (SELECT ROW_NUMBER() OVER (PARTITION BY ho.FromProductionStepId,
												ho.HandoverDatetime,
                                             ho.ObjectTypeId,
                                             ho.ObjectId
                                    ORDER BY ho.CreatedDatetimeUtc) RNumber,
             ho.ObjectTypeId,
             ho.ObjectId,
             ho.FromProductionStepId,
             ho.ToProductionStepId,
             ho.HandoverDatetime,
             ho.HandoverQuantity,
             ho.Note,
             ho.FromDepartmentId,
             ho.ToDepartmentId,
             ho.CreatedDatetimeUtc,
             s.SumHandoverQuantity,
             r.ProductionHandoverReceiptId,
             r.ProductionHandoverReceiptCode
        FROM dbo.ProductionHandover ho
          LEFT JOIN dbo.ProductionHandoverReceipt r ON ho.ProductionHandoverReceiptId = r.ProductionHandoverReceiptId
        JOIN handoverSum s
          ON s.FromProductionStepId = ho.FromProductionStepId
         AND s.HandoverDatetime     = ho.HandoverDatetime
         AND s.ObjectTypeId         = ho.ObjectTypeId
         AND s.ObjectId             = ho.ObjectId
       WHERE ho.IsDeleted = 0),
     handoverHistory
  AS (SELECT            COALESCE(h.ObjectTypeId, ho.ObjectTypeId) ObjectTypeId,
                        COALESCE(h.ObjectId, ho.ObjectId) ObjectId,
                        COALESCE(h.ProductionStepId, ho.FromProductionStepId) FromProductionStepId,
                        COALESCE(h.[Date], ho.HandoverDatetime) [Date],
                        h.ProductionQuantity,
                        ho.HandoverQuantity,
                        ho.SumHandoverQuantity,
                        ho.Note,
                        ISNULL(ho.FromDepartmentId,h.DepartmentId) FromDepartmentId,
                        ho.ToDepartmentId,
                        ho.ToProductionStepId,
                        COALESCE(ho.ProductionHandoverReceiptId, h.ProductionHandoverReceiptId) ProductionHandoverReceiptId,
                        COALESCE(ho.ProductionHandoverReceiptCode, h.ProductionHandoverReceiptCode) ProductionHandoverReceiptCode
        FROM            (   SELECT ROW_NUMBER() OVER (PARTITION BY h.ProductionStepId,
																	h.[Date],
                                                                   h.ObjectTypeId,
                                                                   h.ObjectId
                                                          ORDER BY h.CreatedDatetimeUtc) RNumber,
                                   h.ObjectTypeId,
                                   h.ObjectId,
                                   h.ProductionStepId,
                                   h.[Date],
                                   h.ProductionQuantity,
                                   h.Note,
                                   h.DepartmentId,
                                   h.CreatedDatetimeUtc,
                                   r.ProductionHandoverReceiptId,
                                   r.ProductionHandoverReceiptCode
                              FROM dbo.ProductionHistory h
                                 LEFT JOIN dbo.ProductionHandoverReceipt r ON h.ProductionHandoverReceiptId = r.ProductionHandoverReceiptId
                             WHERE h.IsDeleted = 0) h
        FULL OUTER JOIN handover ho
          ON h.ProductionStepId = ho.FromProductionStepId
         AND h.[Date]           = ho.HandoverDatetime
         AND h.ObjectTypeId     = ho.ObjectTypeId
         AND h.ObjectId         = ho.ObjectId
         AND h.RNumber          = ho.RNumber
         AND h.ProductionHandoverReceiptId = ho.ProductionHandoverReceiptId
         ),
     linkRole
  AS (SELECT s.ContainerId,
             r.ProductionStepId,
             r.ProductionStepLinkDataId,
             r.ProductionStepLinkDataRoleTypeId
        FROM dbo.ProductionStepLinkDataRole r
        JOIN dbo.ProductionStep s
          ON s.ProductionStepId = r.ProductionStepId
       WHERE s.IsDeleted       = 0
         AND s.ContainerTypeId = @CONTAINER_TYPE_PRODUCTION_ORDER),
  linkDataWithTarget AS (    
        SELECT
          d.SubsidiaryId,
          d.ProductionStepLinkDataId,
          d.LinkDataObjectTypeId,
          d.LinkDataObjectId,
          d.Quantity,       

          CASE WHEN d.WorkloadConvertRate IS NOT NULL THEN d.WorkloadConvertRate
          ELSE
            CASE WHEN productivityDetail.WorkLoadTypeId = @WorkloadType_Quantity THEN 1
            ELSE ISNULL(d.ProductPurity,1)
            END
          END WorkloadConvertRate,
          productivityDetail.TargetProductivityId,	
          productivityDetail.TargetProductivity,
          d.StepId,
          d.ProductionStepId,
          d.ContainerId
        FROM (
            SELECT
              d.SubsidiaryId,
              d.ProductionStepLinkDataId,
              d.LinkDataObjectTypeId,
              d.LinkDataObjectId,
              d.Quantity,
              d.WorkloadConvertRate,
              COALESCE(p.TargetProductivityId, ps.TargetProductivityId, @TargetProductivityId_Default) TargetProductivityId,
              ISNULL(p.ProductPurity, ps.ProductPurity) ProductPurity,
              r.ProductionStepId,
              sParent.StepId,
              pStep.ContainerId
            FROM dbo.ProductionStepLinkData d
              JOIN dbo.ProductionStepLinkDataRole r ON r.ProductionStepLinkDataId = d.ProductionStepLinkDataId AND r.ProductionStepLinkDataRoleTypeId = @PRODUCTION_STEP_LINK_DATA_ROLE_TYPE_OUT
              LEFT JOIN dbo.ProductionStep pStep ON pStep.ProductionStepId = r.ProductionStepId
              LEFT JOIN dbo.ProductionStep sParent ON pStep.ParentId = sParent.ProductionStepId
              LEFT JOIN dbo.ProductSemi s ON d.LinkDataObjectId = s.ProductSemiId AND d.LinkDataObjectTypeId = @LINK_DATA_OBJECT_TYPE_SEMI AND s.IsDeleted = 0
              LEFT JOIN [StockDB].dbo.Product p ON d.LinkDataObjectId = p.ProductId AND d.LinkDataObjectTypeId = @LINK_DATA_OBJECT_TYPE_PRODUCT AND p.IsDeleted = 0
              LEFT JOIN [StockDB].dbo.Product ps ON s.RefProductId = ps.ProductId AND ps.IsDeleted = 0
            WHERE d.IsDeleted = 0
