;WITH detailOrder AS (
	
		SELECT
			ROW_NUMBER() OVER (ORDER BY d.ngay_ct, 
				CASE WHEN d.IsDebt = 1 AND d.InputType_InputTypeCode <> @DIEU_CHUYEN_KHO_TYPE THEN 0 ELSE 1 END,
				CASE WHEN d.InputType_InputTypeCode = @DIEU_CHUYEN_KHO_TYPE THEN d.IsDebt ELSE 2 END,
				d.F_Id
			)  stt
			, d.ngay_ct
			, d.so_ct			
			, d.dien_giai
			, d.noi_dung
			, d.IsDebt
			, d.so_luong
			, d.kh
			, d.bo_phan
			, d.vthhtp
			, d.po_code
			, d.order_code
			, d.ma_lsx
			, CASE WHEN d.IsDebt = 1 THEN ISNULL(d.so_luong,0) ELSE 0 END sl_no
			, CASE WHEN d.IsDebt = 1 AND ISNULL(d.so_luong,0) <> 0 THEN ISNULL(d.Vnd_no,0)/d.so_luong ELSE 0 END don_gia_no			
			, CASE WHEN d.IsDebt = 1 THEN ISNULL(d.Vnd_no,0) + ISNULL(thueXnk.vnd,0)	ELSE 0 	END vnd_no	
			, ISNULL(thueXnk.vnd,0) thueXnk	
				
			, CASE WHEN d.IsDebt = 0 THEN ISNULL(d.so_luong,0) 	ELSE 0 	END sl_co
			, CASE WHEN d.IsDebt = 0 AND ISNULL(d.so_luong,0) <> 0 THEN ISNULL(d.Vnd_co,0)/d.so_luong ELSE 0 END don_gia_co
			, CASE WHEN d.IsDebt = 0 THEN ISNULL(d.Vnd_co,0) 	ELSE 0 	END vnd_co
			, d.F_Id								
			, InputBill_F_id InputBill_F_id
			, InputTypeId InputTypeId
			, dbo.afn_customTrim(CONCAT(d.po_code,'','',d.order_code,'','',d.ma_lsx),'','') ghi_chu
			, d.BUT_TOAN
			, d.vthhtp_dvt2 
			, d.so_luong_dv2
			, CASE WHEN d.IsDebt = 1 THEN (CASE WHEN  d.vthhtp_dvt2 IS NOT NULL THEN  ISNULL(d.so_luong_dv2,0) ELSE ISNULL(d.so_luong,0) END) ELSE 0 END sl_dvt2_no
			, CASE WHEN d.IsDebt = 0 THEN (CASE WHEN  d.vthhtp_dvt2 IS NOT NULL THEN  ISNULL(d.so_luong_dv2,0) ELSE ISNULL(d.so_luong,0) END) ELSE 0 END sl_dvt2_co
		FROM dbo._rc_detail d 
		OUTER APPLY
			(
				SELECT 
					SUM(d1.Vnd_no) vnd
				FROM dbo._rc_detail d1
				WHERE d1.InputBill_F_Id = d.InputBill_F_Id 
				AND d1.vthhtp = d.vthhtp 
				AND d1.F_Id = d.F_Id 
				AND d1.BUT_TOAN = 3 
				AND d1.Tk LIKE CONCAT(@Tk,''%'')
				AND d1.IsDebt = 1
			) thueXnk
		OUTER APPLY
			(
				SELECT 
					*
				FROM [dbo].[v_ProductUnitConversion] u1
				WHERE u1.ProductId = d.vthhtp
				AND u1.IsDefault = 1
			) defaulUnitId
		WHERE d.SubsidiaryId = @SubId 
		AND (d.Tk LIKE CONCAT(@Tk,''%'')) 
		AND (ISNULL(@StockId,0) <= 0 OR CASE WHEN d.IsDebt = 1 THEN ISNULL(d.kho_lc, d.kho) ELSE d.kho END = @StockId)
		AND d.ngay_ct BETWEEN @FromDate AND @ToDate 
		AND d.vthhtp = @ProductId
		AND d.BUT_TOAN <> 3

),
 getHeaderData AS(
	 SELECT 
	 F_Id AS F_Id,
	 unitName AS unitName,
	 ISNULL( tonDauKy,0) AS tonDauKy
	 FROM OPENJSON(@HEAD_json_data)
	 WITH(
		 F_Id int ''$.F_Id'',
		 unitName NVARCHAR(50) ''$.unitName''
		 , tonDauKy decimal ''$.tonDauKy''
		 )
)
SELECT
*
FROM (
	SELECT
		d.stt 
		, d.ngay_ct
		, d.so_ct
		, p.PartnerCode ma_kh
		, p.PartnerName ten_kh
		, de.DepartmentCode
		, de.DepartmentName
		, d.dien_giai
		, d.noi_dung
		, d.sl_no
		, d.don_gia_no
		, d.vnd_no
		, d.thueXnk
		, d.sl_co
		, d.don_gia_co
		, d.vnd_co
		, d.F_Id F_Id
		, d.sl_ton
		, 
		
		CASE 
		WHEN d.sl_ton > 0 
		THEN ROUND (d.vnd_ton/d.sl_ton,0)
		ELSE 0 END don_gia_ton
		,
		
		d.vnd_ton

		, InputBill_F_id InputBill_F_id
		, InputTypeId InputTypeId
		, dbo.afn_customTrim(CONCAT(d.po_code,'','',d.order_code,'','',d.ma_lsx),'','') ghi_chu,
		d.BUT_TOAN
		, CASE WHEN d.vthhtp_dvt2 IS NULL THEN prd.UnitId_UnitName ELSE u.ProductUnitConversionName END AS dvt2
		, d.sl_dvt2_no
		, d.sl_dvt2_co
		, d.sl_dvt2_ton
		, d.vthhtp_dvt2
	FROM (
		SELECT * ,
			@HEAD_sl_dau_ky  + SUM(CASE WHEN d.IsDebt = 1 THEN ISNULL(d.so_luong,0) ELSE -ISNULL(d.so_luong,0) END) OVER (ORDER BY d.stt) 
				AS sl_ton,
			@HEAD_vnd_dau_ky + SUM(CASE WHEN d.IsDebt = 1 THEN ISNULL(d.Vnd_no,0) ELSE -ISNULL(d.Vnd_co,0) END) OVER (ORDER BY d.stt)
				AS vnd_ton,
			CASE WHEN d.vthhtp_dvt2 IS NOT NULL THEN
				 (ISNULL( (SELECT g.tonDauKy FROM getHeaderData g WHERE d.vthhtp_dvt2 = g.F_Id ),0)  + SUM(CASE WHEN d.IsDebt =1 THEN ISNULL(d.so_luong_dv2,0) ELSE- ISNULL(d.so_luong_dv2,0) END ) OVER (PARTITION BY d.vthhtp_dvt2 ORDER BY d.stt)) ELSE 
			@HEAD_sl_dau_ky  + SUM(CASE WHEN d.IsDebt = 1 THEN ISNULL(d.so_luong,0) ELSE -ISNULL(d.so_luong,0) END) OVER (ORDER BY d.stt) END
			 	AS sl_dvt2_ton
		FROM 
		detailOrder d 
	) d
	LEFT JOIN [dbo].[v_Partner] p ON d.kh = p.F_Id
	LEFT JOIN [dbo].[v_Department] de ON d.bo_phan = d.F_Id
	LEFT JOIN [dbo].[v_Product] prd ON d.vthhtp = prd.F_Id
	LEFT JOIN [dbo].[v_ProductUnitConversion] u ON u.F_Id = d.vthhtp_dvt2
) d
$FILTER::WHERE
ORDER BY d.stt

