                        SUM(CASE
                                WHEN d.BUT_TOAN <> 3 AND d.ngay_ct < @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN d.so_luong
                                                                        ELSE -d.so_luong END
                                ELSE NULL END) AS so_luong_dau,

