                                                                        ELSE -d.so_luong_dv2 END
                                ELSE NULL END) AS so_luong_dau2,

                        SUM(CASE
                                WHEN d.ngay_ct < @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN d.Vnd_no
                                                                        ELSE -d.Vnd_co END
                                ELSE NULL END) AS vnd_dau,

                        SUM(CASE
                                WHEN d.BUT_TOAN <> 3 AND  d.ngay_ct >= @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN d.so_luong
                                                                        ELSE NULL END
                                ELSE NULL END) AS so_luong_nhap,
						SUM(CASE
                                WHEN d.BUT_TOAN <> 3 AND  d.ngay_ct >= @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN d.so_luong_dv2
                                                                        ELSE NULL END
                                ELSE NULL END) AS so_luong_nhap2,

                        SUM(CASE
                                WHEN d.ngay_ct >= @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN d.Vnd_no
                                                                        ELSE NULL END
                                ELSE NULL END) AS vnd_nhap,
                        SUM(CASE
                                WHEN d.BUT_TOAN <> 3 AND  d.ngay_ct >= @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN NULL
                                                                        ELSE d.so_luong END
                                ELSE NULL END) AS so_luong_xuat,
						SUM(CASE
                                WHEN d.BUT_TOAN <> 3 AND  d.ngay_ct >= @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN NULL
                                                                        ELSE d.so_luong_dv2 END
                                ELSE NULL END) AS so_luong_xuat2,


                        SUM(CASE
                                WHEN d.ngay_ct >= @FromDate THEN CASE d.IsDebt
                                                                        WHEN 1 THEN NULL
                                                                        ELSE d.Vnd_co END
                                ELSE NULL END) AS vnd_xuat,

                        SUM(CASE
                                WHEN d.BUT_TOAN <> 3 THEN CASE d.IsDebt
                                                                WHEN 1 THEN d.so_luong
                                                                ELSE -d.so_luong END
                                ELSE NULL END) AS so_luong_cuoi,

