                                WHEN 1 THEN d.Vnd_no
                                ELSE -d.Vnd_co END) AS vnd_cuoi

                FROM (   SELECT      --ROW_NUMBER() OVER (PARTITION BY d.F_Id ORDER BY d.ngay_ct) InRowNumber,
                                        d.ngay_ct,
                                        d.Vnd_co,
                                        d.Vnd_no,
                                        d.so_luong,
					--CASE WHEN pu.IsDefault = 1 OR pu.F_Id IS NULL THEN d.so_luong ELSE d.so_luong_dv2 END so_luong_dv2,
                                        ISNULL(d.so_luong_dv2, d.so_luong) so_luong_dv2,
