                                AND      (   ISNULL(@StockId, 0)                  <= 0
                                        OR   CASE WHEN d.IsDebt = 1 THEN ISNULL(d.kho_lc, d.kho) ELSE d.kho END = @StockId)
                                AND      (   LEN(ISNULL(@vthhtp_productCode, '''')) = 0
                                        OR   p.ProductCode                        = @vthhtp_productCode)
                --AND (d.so_luong >0 OR CASE d.IsDebt WHEN 1 THEN d.Vnd_no ELSE d.Vnd_co END>0)
                ) d
                GROUP BY d.vthhtp,
                        d.ProductCode,
                        d.ProductName,
                        d.UnitId_UnitName,
						d.ProductUnitConversionId,
						d.ProductUnitConversionName
						) AS v
        WHERE (   so_luong_dau <> 0
        OR   vnd_dau <> 0
        OR   so_luong_nhap <> 0
        OR   vnd_nhap <> 0
