                                        (SUM(CASE d.IsDebt
                                        WHEN 1 THEN d.Vnd_no
                                        ELSE -d.Vnd_co END))
                                                /
                                        (SUM(CASE
                                        WHEN d.BUT_TOAN <> 3 THEN CASE d.IsDebt
                                                                WHEN 1 THEN d.so_luong_dv2
                                                                ELSE -d.so_luong_dv2 END
                                        ELSE NULL END))
                                ELSE 0
                                END AS don_gia_CD,

                        SUM(CASE d.IsDebt
                                WHEN 1 THEN d.Vnd_no
                                ELSE -d.Vnd_co END) AS vnd_cuoi

                FROM (   SELECT      --ROW_NUMBER() OVER (PARTITION BY d.F_Id ORDER BY d.ngay_ct) InRowNumber,
                                        d.ngay_ct,
                                        d.Vnd_co,
                                        d.Vnd_no,
                                        d.so_luong,
					--CASE WHEN pu.IsDefault = 1 OR pu.F_Id IS NULL THEN d.so_luong ELSE d.so_luong_dv2 END so_luong_dv2,
                                        ISNULL(d.so_luong_dv2, d.so_luong) so_luong_dv2,
                                        d.BUT_TOAN,
                                        d.IsDebt,
                                        d.vthhtp,
                                        p.ProductCode,
                                        p.ProductName,
                                        p.UnitId_UnitName,
					CASE WHEN pu.IsDefault = 1 OR pu.F_Id IS NULL THEN NULL ELSE pu.F_Id END ProductUnitConversionId,
					CASE WHEN pu.IsDefault = 1 OR pu.F_Id IS NULL THEN p.UnitId_UnitName ELSE pu.ProductUnitConversionName END  ProductUnitConversionName
                                        
                                FROM      dbo._rc_detail d
                                LEFT JOIN dbo.v_Product p ON d.vthhtp = p.F_Id
								LEFT JOIN dbo.v_ProductUnitConversion pu ON d.vthhtp_dvt2 = pu.F_Id
                        WHERE      d.SubsidiaryId                      = @SubId
                                AND      d.ngay_ct                                <= @ToDate
                                AND      d.vthhtp IS NOT NULL
                                AND      (   LEN(ISNULL(@Tk, ''''))                 = 0
                                        OR   d.Tk LIKE @Tk + ''%'')
                                AND      (   ISNULL(@StockId, 0)                  <= 0
                                        OR   CASE WHEN d.IsDebt = 1 THEN ISNULL(d.kho_lc, d.kho) ELSE d.kho END = @StockId)
                                AND      (   LEN(ISNULL(@vthhtp_productCode, '''')) = 0
                                        OR   p.ProductCode                        = @vthhtp_productCode)
                --AND (d.so_luong >0 OR CASE d.IsDebt WHEN 1 THEN d.Vnd_no ELSE d.Vnd_co END>0)
                ) d
                GROUP BY d.vthhtp,
                        d.ProductCode,
                        d.ProductName,
                        d.UnitId_UnitName,
						d.ProductUnitConversionId,
						d.ProductUnitConversionName
						) AS v
        WHERE (   so_luong_dau <> 0
        OR   vnd_dau <> 0
        OR   so_luong_nhap <> 0
