DECLARE @DuNoDauKy DECIMAL(18,5);
DECLARE @DuCoDauKy DECIMAL(18,5);
SELECT
            @DuNoDauKy = ISNULL(SUM(CASE WHEN bc_kh.Vnd > 0 THEN bc_kh.Vnd ELSE 0 END),0)
        FROM
        (
            SELECT 
                (SUM(ISNULL(r.Vnd_no,0)) - SUM(ISNULL(r.Vnd_co,0))) Vnd
            FROM _rc_detail r 
            WHERE r.SubsidiaryId = @SubId AND r.Tk LIKE ''1331%'' AND r.Ngay_ct < @FromDate 
            GROUP BY r.kh
        ) bc_kh

SELECT
            @DuCoDauKy = ISNULL(SUM(CASE WHEN bc_kh.Vnd < 0 THEN -bc_kh.Vnd ELSE 0 END),0)
        FROM
        (
            SELECT 
                (SUM(ISNULL(r.Vnd_no,0)) - SUM(ISNULL(r.Vnd_co,0))) Vnd
            FROM _rc_detail r 
            WHERE r.SubsidiaryId = @SubId AND r.Tk LIKE ''33311'' AND r.Ngay_ct < @FromDate 
            GROUP BY r.kh
        ) bc_kh

