INSERT INTO [dbo].[ReportType] ([ReportTypeId], [ReportTypeGroupId], [ReportTypeName], [ReportPath], [SortOrder], [CreatedByUserId], [CreatedDatetimeUtc], [UpdatedByUserId], [UpdatedDatetimeUtc], [IsDeleted], [DeletedDatetimeUtc], [MainView], [Joins], [Wheres], [OrderBy], [Head], [Footer], [HeadSql], [BodySql], [FooterSql], [PrintTitle], [GroupColumns], [Sign], [HtmlTemplate], [DetailReportId], [DetailReportParams], [OnLoadJsCode], [PreLoadDataJsCode], [AfterLoadDataJsCode], [OnCloseJsCode], [Columns], [IsBsc], [BscConfig], [HeadPrint], [OnCellClickJsCode], [OnCellChangeValueJsCode], [TemplateFileId], [DetailOpenTypeId], [DetailTargetId], [GroupTitleSql], [IsDbPaging], [DisplayConfig], [DetailReportJsCodeCanOpenTarget], [ReplicatedFromReportTypeId]) VALUES (269, 51, N'Chi phí  sản xuất dở dang trực tiếp', N'1', 0, 88, '2023-08-11 08:09:33.5404066', 88, '2023-08-11 08:09:33.5404096', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'DECLARE @PRODUCTION_ORDER_STATUS_FINISHED INT = 350
DECLARE @PRODUCTION_ORDER_STATUS_COMPLETED INT = 400

;WITH po AS(
		SELECT 
			po.ProductionOrderId, 
			po.ProductionOrderCode, 
			po.ProductionOrderStatus,
			po.[Date], 
			pod.ProductId, 
			STRING_AGG(pod.OrderCode, '', '') OrderCodes,
			SUM(pod.Quantity) Quantity,
			SUM(pod.Quantity + pod.ReserveQuantity) TotalQuantity
		FROM [ManufacturingDB].[dbo].ProductionOrderDetail pod 
		JOIN [ManufacturingDB].[dbo].ProductionOrder po ON pod.ProductionOrderId = po.ProductionOrderId
		WHERE po.[Date] <= @ToDate AND po.IsDeleted = 0 AND pod.IsDeleted = 0
		GROUP BY po.ProductionOrderId, po.ProductionOrderCode, po.ProductionOrderStatus, po.[Date], pod.ProductId
), nhap_kho AS
(
	SELECT
		rc.vthhtp,
		rc.ma_lsx,
		SUM(CASE WHEN rc.ngay_ct<@FromDate THEN rc.so_luong ELSE 0 END)  ky_truoc,
		SUM(CASE WHEN rc.ngay_ct BETWEEN @FromDate AND @ToDate THEN rc.so_luong ELSE 0 END) trong_ky,
		SUM(CASE WHEN rc.ngay_ct<=@ToDate THEN rc.so_luong ELSE 0 END) ky_truoc_va_ky_nay,
		SUM(rc.so_luong) tong
	FROM [AccountancyDB].[dbo]._rc rc 
	WHERE rc.ma_lsx IS NOT NULL AND rc.tk_co LIKE ''154%'' AND rc.tk_no LIKE ''155%'' --AND rc.ngay_ct <= @ToDate
	GROUP BY rc.vthhtp,
             rc.ma_lsx
), do_dang_ky_truoc AS
(
	--Lenh hoan thanh ky truoc: nhap_kho.ky_truoc >= Quantity Hoac khong co nhap kho ky nay va trang thai hoan thanh
	--=> Do dang ky truoc nhap_kho.ky_truoc < Quantity hoac trang thai chua hoan thanh
	-- + lay lenh trong ky
	SELECT
		DISTINCT
		po.*,
		CASE WHEN ISNULL(nhap_kho.ky_truoc_va_ky_nay,0) >= ISNULL(po.Quantity,0) THEN 1
			 WHEN ISNULL(nhap_kho.ky_truoc_va_ky_nay,0) = ISNULL(nhap_kho.tong,0) AND po.ProductionOrderStatus IN (@PRODUCTION_ORDER_STATUS_FINISHED, @PRODUCTION_ORDER_STATUS_COMPLETED) 
				THEN 1 
			 ELSE 0
		END IsCompleted
	FROM po
	LEFT JOIN nhap_kho ON po.ProductId = nhap_kho.vthhtp AND po.ProductionOrderCode = nhap_kho.ma_lsx
	WHERE
		CASE WHEN ISNULL(nhap_kho.ky_truoc_va_ky_nay,0) >= ISNULL(po.Quantity,0) THEN 1
			 WHEN ISNULL(nhap_kho.ky_truoc_va_ky_nay,0) = ISNULL(nhap_kho.tong,0) AND po.ProductionOrderStatus IN (@PRODUCTION_ORDER_STATUS_FINISHED, @PRODUCTION_ORDER_STATUS_COMPLETED) 
				THEN 1 
			 ELSE 0
		END = 0
	OR po.[Date] >= @FromDate
), chi_phi AS
(
	SELECT
		rc.ma_lsx,
		SUM(CASE WHEN rc.ngay_ct < @FromDate THEN rc.vnd ELSE 0 END) cp_ky_truoc,
		SUM(CASE WHEN rc.ngay_ct BETWEEN @FromDate AND @ToDate THEN rc.vnd ELSE 0 END) cp_trong_ky,
		SUM(rc.vnd) tong_cp
	FROM [AccountancyDB].[dbo]._rc rc
	WHERE (
		rc.tk_no LIKE ''621%'' OR rc.tk_no LIKE ''622%'' OR rc.tk_no LIKE ''627%''
		)
		AND rc.ma_lsx IS NOT NULL       
        
	GROUP BY rc.ma_lsx
), v AS
(
	SELECT
		CASE WHEN ProductSortOrder=1 THEN 1 ELSE 0 END IsFirst,
		*
	FROM (

			SELECT
				ROW_NUMBER() OVER(PARTITION BY v.ProductionOrderId ORDER BY v.ProductId) ProductSortOrder,	

				v.ProductionOrderId,--group
				v.ProductionOrderCode,--group
				v.[Date],--group
				v.ProductId,
				v.OrderCodes,
				v.Quantity,
				p.ProductCode,
				p.ProductName,
				p.UnitId_UnitName,

				v.ky_truoc_sl_nhan_vao,
				v.ky_truoc_cp_nhan_vao,--group
				v.ky_truoc_sl_nhap_kho,
				v.ky_truoc_cp_nhap_kho,
				v.trong_ky_sl_nhan_vao,
				v.trong_ky_cp_nhan_vao,--group
				v.trong_ky_sl_nhap_kho,		
				v.trong_ky_cp_nhap_kho,
				v.cuoi_ky_so_luong,
				v.tong_cp - ISNULL(v.ky_truoc_cp_nhap_kho,0) - ISNULL(v.trong_ky_cp_nhap_kho,0) [cuoi_ky_cp]
			FROM (
				SELECT 
					po.ProductionOrderId,--group
					po.ProductionOrderCode,--group
					po.[Date],--group
					po.ProductId,
					po.OrderCodes,
					po.Quantity,
	
					CASE WHEN po.[Date]<@FromDate THEN po.Quantity ELSE NULL END	ky_truoc_sl_nhan_vao,
					po.cp_ky_truoc													ky_truoc_cp_nhan_vao,--group
					nhap_kho.ky_truoc												ky_truoc_sl_nhap_kho,
					(po.tong_cp/po.Quantity) * nhap_kho.ky_truoc					ky_truoc_cp_nhap_kho,

					CASE WHEN po.[Date] BETWEEN @FromDate AND @ToDate 
						AND po.IsCompleted = 0 THEN po.Quantity ELSE NULL 
					END																trong_ky_sl_nhan_vao,
					po.cp_trong_ky													trong_ky_cp_nhan_vao,--group
					nhap_kho.trong_ky												trong_ky_sl_nhap_kho,
					CASE WHEN po.IsCompleted = 0 
						THEN (po.tong_cp/po.Quantity) * nhap_kho.trong_ky
						ELSE po.tong_cp - ISNULL((po.tong_cp/po.Quantity) * nhap_kho.ky_truoc,0)
					END																trong_ky_cp_nhap_kho,
	
					CASE WHEN po.IsCompleted = 0 
						THEN po.Quantity - ISNULL(nhap_kho.ky_truoc_va_ky_nay,0)
						ELSE nhap_kho.tong - ISNULL(nhap_kho.ky_truoc_va_ky_nay,0)
					END																cuoi_ky_so_luong,
					po.tong_cp--group
				FROM (
					SELECT
						po.*,
						cp.tong_cp * (po.Quantity/t.TotalQuantity)		tong_cp,
						cp.cp_ky_truoc  * (po.Quantity/t.TotalQuantity) cp_ky_truoc,
						cp.cp_trong_ky  * (po.Quantity/t.TotalQuantity) cp_trong_ky
					FROM do_dang_ky_truoc po
						JOIN (
							SELECT
								o.ProductionOrderId,
								SUM(d.Quantity) TotalQuantity
							FROM ManufacturingDB.dbo.ProductionOrder o
							JOIN ManufacturingDB.dbo.ProductionOrderDetail d ON d.ProductionOrderId = o.ProductionOrderId
							WHERE o.IsDeleted = 0 AND d.IsDeleted = 0
							GROUP BY o.ProductionOrderId
						) t ON po.ProductionOrderId = t.ProductionOrderId

						LEFT JOIN chi_phi cp ON po.ProductionOrderCode = cp.ma_lsx
					) po
					LEFT JOIN nhap_kho ON nhap_kho.ma_lsx = po.ProductionOrderCode AND nhap_kho.vthhtp = po.ProductId
				WHERE po.Quantity>0
	
			) v
			JOIN dbo.v_Product p ON v.ProductId = p.F_Id		
	) v
), vSort AS (
	SELECT
		v.ProductionOrderId,		
		MAX(v.ProductionOrderCode) ProductionOrderCode,
		MAX(v.[Date]) [Date],
		MAX(v.ProductId) ProductId,
		MAX(v.OrderCodes) OrderCodes,
		MAX(v.Quantity) Quantity,
		MAX(v.ProductCode) ProductCode,
		MAX(v.ProductName) ProductName,
		MAX(v.UnitId_UnitName) UnitId_UnitName,		
		MAX(v.ky_truoc_sl_nhan_vao) ky_truoc_sl_nhan_vao,
		MAX(v.ky_truoc_cp_nhan_vao) ky_truoc_cp_nhan_vao,--group
		MAX(v.ky_truoc_sl_nhap_kho) ky_truoc_sl_nhap_kho,
		MAX(v.ky_truoc_cp_nhap_kho) ky_truoc_cp_nhap_kho,
		MAX(v.trong_ky_sl_nhan_vao) trong_ky_sl_nhan_vao,
		MAX(v.trong_ky_cp_nhan_vao) trong_ky_cp_nhan_vao,--group
		MAX(v.trong_ky_sl_nhap_kho) trong_ky_sl_nhap_kho,		
		MAX(v.trong_ky_cp_nhap_kho) trong_ky_cp_nhap_kho,
		MAX(v.cuoi_ky_so_luong) cuoi_ky_so_luong,
		MAX(cuoi_ky_cp) cuoi_ky_cp
	FROM v	
	GROUP BY v.ProductionOrderId
)
